+ (NSString *)toHanyuPinyinStringWithNSString:(NSString *)str
                  withHanyuPinyinOutputFormat:(HanyuPinyinOutputFormat *)outputFormat
                                 withNSString:(NSString *)seperater {
    NSMutableString *resultPinyinStrBuf = [[NSMutableString alloc] init];
    BOOL bLastPinYin = FALSE;
    for (int i = 0; i <  str.length; i++) {
        NSString *mainPinyinStrOfChar = [PinyinHelper getFirstHanyuPinyinStringWithChar:[str characterAtIndex:i] withHanyuPinyinOutputFormat:outputFormat];
        if (nil != mainPinyinStrOfChar) {
           if (bLastPinYin && i) [resultPinyinStrBuf appendString:seperater];
            [resultPinyinStrBuf appendString:mainPinyinStrOfChar];
            bLastPinYin = FALSE;
            if (i != [str length] - 1) {
                [resultPinyinStrBuf appendString:seperater];
            }
        }
        else {
            bLastPinYin = YES;
            [resultPinyinStrBuf appendFormat:@"%C",[str characterAtIndex:i]];
        }
    }
    return resultPinyinStrBuf;
}

+ (NSString *)getFirstHanyuPinyinStringWithChar:(unichar)ch
                    withHanyuPinyinOutputFormat:(HanyuPinyinOutputFormat *)outputFormat {
    NSArray *pinyinStrArray = [PinyinHelper getFormattedHanyuPinyinStringArrayWithChar:ch withHanyuPinyinOutputFormat:outputFormat];
    if ((nil != pinyinStrArray) && ((int) [pinyinStrArray count] > 0)) {
        return [pinyinStrArray objectAtIndex:0];
    }
    else {
        return nil;
    }
}

+ (NSString*)getPinYin:(NSString *)str shortedPinyin:(NSMutableString *)shortedPinyin withNSString:(NSString *)seperater{
    HanyuPinyinOutputFormat *outputFormat=[[HanyuPinyinOutputFormat alloc] init];
    [outputFormat setToneType:ToneTypeWithoutTone];
    [outputFormat setVCharType:VCharTypeWithV];
    [outputFormat setCaseType:CaseTypeLowercase];
    
    NSMutableString *resultPinyinStrBuf = [[NSMutableString alloc] init];
    BOOL bLastPinYin = FALSE;
    BOOL bLastPinYinSep = FALSE;
    for (int i = 0; i <  str.length; i++) {
        unichar c = [str characterAtIndex:i];
        NSString *mainPinyinStrOfChar = [PinyinHelper getFirstHanyuPinyinStringWithChar:c withHanyuPinyinOutputFormat:outputFormat];
        if (nil != mainPinyinStrOfChar) {
            if (seperater && bLastPinYin && i) [resultPinyinStrBuf appendString:seperater];
            [resultPinyinStrBuf appendString:mainPinyinStrOfChar];
            bLastPinYin = FALSE;
            if (seperater && i != [str length] - 1) {
                [resultPinyinStrBuf appendString:seperater];
            }
            
            if (shortedPinyin && mainPinyinStrOfChar.length) {
                [shortedPinyin appendFormat:@"%C", [mainPinyinStrOfChar characterAtIndex:0]];
            }
        }
        else {
            if (isalnum(c)) {
                [resultPinyinStrBuf appendFormat:@"%C", c];
                if (!bLastPinYin || (bLastPinYinSep && bLastPinYin)) [shortedPinyin appendFormat:@"%C", c];
                bLastPinYinSep = FALSE;
            }else {
                if (seperater) [resultPinyinStrBuf appendString:seperater];
                bLastPinYinSep = YES;
            }
            
            bLastPinYin = YES;
        }
    }
    return resultPinyinStrBuf;
}

- (id)init {
    return [super init];
}
+ (char)getFirstChar:(NSString*)str {
    if (nil == str || 0 == [str length]) {
        return '#';
    }
    const char * firstChar = [str UTF8String];
    if ( ('a'<=  *firstChar && *firstChar <= 'z')
        || ('A' <= *firstChar && *firstChar <= 'Z')) {
        return *firstChar;
    }
    return pinyinFirstLetter([str characterAtIndex:0]);
}
@end
